import React, { useState } from "react";
import "./Chat.scss";

function Chat() {
  const [messages, addMessage] = useState([]);

  return (
    <div className="Chat">
      <div className="message"></div>
      <input onSubmit={addMessage}></input>
    </div>
  );
}

export default Chat;
