import React, { useRef, useEffect } from "react";
import "./Canvas.scss";

import CanvasDraw from "react-canvas-draw";

function Canvas() {
  const canvas = useRef();

  useEffect(() => {
    console.log(canvas.current.getSaveData());
  });

  return (
    <section className="Canvas">
      <CanvasDraw ref={canvas} />
      <button>Save</button>
    </section>
  );
}

export default Canvas;
