import React, { useState } from "react";
import "./Chat.scss";
import Message from "../Message/Message";

function Chat() {
  const [messages, addMessage] = useState([<Message />]);

  const submitMessage = e => {
    if (e.charCode === 13) {
      addMessage([...messages]);
    }
  };

  return (
    <div className="Chat">
      <div className="message"></div>
      {}
      <input onKeyPress={submitMessage}></input>
    </div>
  );
}

export default Chat;
