import React, { useState, useContext, useEffect } from "react";
import "./StatusMessage.scss";
import SocketContext from "../../context";
import { useSelector, useDispatch } from "react-redux";

function StatusMessage() {
  let { socket } = useContext(SocketContext);
  const [gameStarted, setGameStarted] = useState(false);
  const [status, setStatus] = useState("");
  const drawer = useSelector(state => state.canDrawReducer);
  const dispatch = useDispatch();
  const { room } = useSelector(state => state.contactReducer);

  useEffect(() => {
    if (gameStarted) {
      socket.emit("chatMessage", {
        name: "admin",
        room,
        input: "GAME STARTED!"
      });
    }
  }, [status]);

  // sockets
  socket.on("gameStart", () => {
    console.log("game started");
    setStatus("Game In Progress");
    setGameStarted(true);
  });

  socket.on("gameEnded", () => {
    console.log("game ended");
    setStatus("Game Not Started");
    dispatch({ type: "SET_DRAW", payload: false });
    setGameStarted(false);
  });

  useEffect(() => {
    if (!gameStarted) {
      setStatus("Game Not Started");
    } else {
      setStatus("Game In Progress");
    }
  }, [drawer, gameStarted]);

  return <div className="StatusMessage">{status}</div>;
}

export default StatusMessage;
