import "./Chat.scss";

function Chat() {
  const [messages, addMessage] = useState([<h1>Hello</h1>, <h1>Bye</h1>]);

  const submitMessage = () => {
    console.log("message");
  };

  return (
    <div className="Chat">
      <div className="message"></div>
      {messages}
      <input onKeyDown={submitMessage}></input>
    </div>
  );
}

export default Chat;
