import React, { useEffect } from "react";
import "./Message.scss";

import Emoji from "react-emoji-render";
import { EmoteFetcher, EmoteParser } from "twitch-emoticons";

function Message({ name, message, src }) {
  const fetcher = new EmoteFetcher();
  const parser = new EmoteParser(fetcher, {
    type: "markdown",
    match: /:(.+?):/g
  });

  useEffect(() => {
    fetcher.fetchTwitchEmotes().then(() => {
      const kappa = fetcher.emotes.get("Kappa").toLink();
      console.log(kappa);
      // https://static-cdn.jtvnw.net/emoticons/v1/25/1.0

      const text = "Hello :PogChamp:!";
      const parsed = parser.parse(text);
      console.log(parsed);
      // Hello ![PogChamp](https://static-cdn.jtvnw.net/emoticons/v1/88/1.0 "PogChamp")!
    });
  }, []);

  return (
    <>
      <div className="MessageContainer">
        <img
          src={src || "https://www.w3schools.com/w3images/bandmember.jpg"}
          alt="https://www.w3schools.com/w3images/bandmember.jpg"
        ></img>
        <div className="Message">
          <h4>{name || "Guest"}</h4>
          <p>
            <Emoji
              text={message || "You need to enable JavaScript to run this app."}
            />
          </p>
        </div>
      </div>
    </>
  );
}

export default Message;
