import React from "react";
import "./App.css";

import { BrowserRouter, Route } from "react-router-dom";
import Landing from "./components/Landing/Landing";
import Canvas from "./components/Canvas/Canvas";
import Join from "./components/Join/Join";
import SocketProvider from "./provider";
import RoomList from "./components/RoomList/RoomList";
import Login from "./components/Login/Login";
import Logout from "./components/Logout/Logout";
import NotificationModule from "./components/NotificationModule/NotificationModule";

import LoadingSpinner from "./components/LoadingSpinner/LoadingSpinner";
import { useSelector } from "react-redux";

function App() {
  const loadingReducer = useSelector(state => state.currentSpinnerModeReducer);
  const isLoading = loadingReducer ? "Block" : "None";
  console.log(isLoading);
  return (
    <SocketProvider>
      <div className="App">
        <LoadingSpinner style={{ display: isLoading }} />
        <NotificationModule />
        <BrowserRouter>
          <Route path="/" exact>
            <Landing />
          </Route>
          <Route path="/join" exact>
            <Join />
          </Route>
          <Route path="/canvas" exact>
            <Canvas />
          </Route>
          <Route path="/roomlist" exact>
            <RoomList />
          </Route>
          <Route path="/login" exact>
            <Login />
          </Route>
          <Route path="/logout" exact>
            <Logout />
          </Route>
        </BrowserRouter>
      </div>
    </SocketProvider>
  );
}

export default App;
