import React, { useRef, useEffect, useState } from "react";
import "./Canvas.scss";
function Canvas() {
  const [context, setContext] = useState();
  const canvas = useRef(context);
  let mouseClicked = false;
  let ctx, socket, currX, currY, prevX, prevY;

  const handleMouseDown = e => {
    mouseClicked = !mouseClicked;
  };

  const handleMouseUp = e => {
    mouseClicked = !mouseClicked;
  };

  const handleMouseMove = e => {
    if (mouseClicked) {
      console.log("hello");
      context.beginPath();
      context.arc(e.clientX, e.clientY, 7.5, 0, Math.PI * 1, false);
      context.lineWidth = 2;
      context.strokeStyle = "#000000";
      context.stroke();
    }
  };

  const findxy = (res, e) => {
    if (drawPerm == true) {
      if (res == "down") {
        prevX = currX;
        prevY = currY;
        currX = e.clientX - canvas.offsetLeft;
        currY = e.clientY - canvas.offsetTop;

        flag = true;
        dot_flag = true;
        if (dot_flag) {
          ctx.beginPath();
          ctx.fillStyle = x;
          ctx.fillRect(currX, currY, 2, 2);
          ctx.closePath();
          dot_flag = false;
        }
      }

      if (res == "up" || res == "out") {
        flag = false;
      }
      if (res == "move") {
        if (flag) {
          prevX = currX;
          prevY = currY;
          currX = e.clientX - canvas.offsetLeft;
          currY = e.clientY - canvas.offsetTop;
          draw();
        }

        function removeDraw() {
          console.log("removeDraw");

          canvas.addEventListener(
            "mousemove",
            function(e) {
              findxy("", e);
            },
            false
          );
          canvas.addEventListener(
            "mousedown",
            function(e) {
              findxy("", e);
            },
            false
          );
          canvas.addEventListener(
            "mouseup",
            function(e) {
              findxy("", e);
            },
            false
          );
          canvas.addEventListener(
            "mouseout",
            function(e) {
              findxy("", e);
            },
            false
          );
        }

        function draw() {
          ctx.beginPath();
          ctx.moveTo(prevX, prevY);
          ctx.lineTo(currX, currY);
          ctx.strokeStyle = x;
          ctx.lineWidth = y;
          ctx.stroke();
          ctx.closePath();
          // send draw data
          socket.emit("draw", { currX, currY, prevX, prevY });
        }
      }
    }
  };

  useEffect(() => {
    setContext(canvas.current.getContext("2d"));
  }, [canvas]);

  return (
    <section className="Canvas">
      <canvas
        ref={canvas}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        Please update your browser
      </canvas>
    </section>
  );
}

export default Canvas;
