import React, { useEffect, useContext, useState } from "react";
import "./RoomList.scss";
import SocketContext from "../../context";
import { useHistory } from "react-router";
import { useDispatch } from "react-redux";

function RoomList() {
  const history = useHistory();
  const dispatch = useDispatch();

  let { socket } = useContext(SocketContext);
  let [rooms, updateRoomList] = useState([]);
  const [room, setRoom] = useState(12);
  const localStorageData = JSON.parse(localStorage.getItem("loginUserInfo"));

  let [title, changeTitle] = useState("No Rooms");
  useEffect(() => {
    socket.emit("getAllRooms");
  }, [socket]);

  useEffect(() => {
    socket.on("sendAllRooms", res => {
      console.log(res);
      if (res.length > 0) {
        changeTitle("Rooms");
      }
      updateRoomList(res);
    });
  }, [socket, rooms]);

  const redirect = room => {
    if (localStorageData) {
      dispatch({
        type: "SET_INFO",
        payload: { name: localStorageData.name, room }
      });
      history.push("/canvas");
    } else {
      history.push("/login");
    }
  };

  const newRoom = () => {
    if (localStorageData) {
      setRoom(Math.floor(Math.random() * 101));

      dispatch({
        type: "SET_INFO",
        payload: { name: localStorageData.name, room }
      });
    }
    history.push("/canvas");
  };

  const renderRooms = () => {
    if (rooms.length > 0) {
      return rooms.map((i, index) => {
        return (
          <div className="list-item" key={index}>
            <h3>Room {i.roomId || "Room Name"}</h3>
            <h4>{i.users.length}/4</h4>
            <button onClick={e => redirect(i.roomId)}>
              {i.users.length > 4 ? "FULL" : "Join"}
            </button>
          </div>
        );
      });
    }
  };

  return (
    <div className="RoomList">
      <div className="Container">
        <h1>{title}</h1>
        {renderRooms()}
      </div>
      <div onClick={newRoom} className="myButton">
        New Room
      </div>
    </div>
  );
}

export default RoomList;
