import React, { useRef, useState, useEffect } from "react";
import "./Canvas.scss";
import { isMobile } from "react-device-detect";
import CanvasDraw from "react-canvas-draw";

import io from "socket.io-client";

function Canvas() {
  const canvas = useRef();
  const [saved, updatedSaved] = useState();

  useEffect(() => {
    const socket = io.connect("http://localhost:5000/");
  }, []);

  socket.on("updateData", data => {
    console.log("123");
    // if (data) {
    //   console.log(data);
    //   canvas.current.loadSaveData(data, false);
    // }
  });

  const undo = () => {
    canvas.current.undo();
  };

  const load = () => {
    if (saved) canvas.current.loadSaveData(saved, false);
  };

  const handleOnChange = () => {
    updatedSaved(canvas.current.getSaveData());

    socket.emit("drawingData", saved);
  };

  return (
    <section className="Canvas">
      {!isMobile ? (
        <div>
          <CanvasDraw
            ref={canvas}
            canvasWidth={1200}
            canvasHeight={550}
            disabled={false}
            lazyRadius={0}
            hideInterface={false}
            onChange={handleOnChange}
          />
          <button onClick={load}>Load</button>
          <button onClick={undo}>Undo</button>
        </div>
      ) : (
        <h1>Mobile not supported</h1>
      )}
    </section>
  );
}

export default Canvas;
