import React, { useState } from "react";
import "./Chat.scss";
import Message from "../Message/Message";

function Chat() {
  const [messages, addMessage] = useState([<Message />]);

  const submitMessage = e => {
    if (e.charCode === 13) {
      addMessage([...messages, <Message key={messages.length + 1} />]);
    }
  };

  return (
    <div className="Chat">
      <div className="message">
        {messages.map((e, index) => {
          return e;
        })}
      </div>
      <input onKeyPress={submitMessage}></input>
    </div>
  );
}

export default Chat;
