import React, { useRef, useEffect, useState } from "react";
import "./Canvas.scss";
function Canvas() {
  const [ctx, setContext] = useState();
  const canvas = useRef(ctx);
  let mouseClicked = false;
  let socket, currX, currY, prevX, prevY, flag, dot_flag, x, y;

  const handleMouseDown = e => {
    ctx.beginPath();
    ctx.moveTo(prevX, prevY);
    ctx.lineTo(currX, currY);
    ctx.strokeStyle = x;
    ctx.lineWidth = y;
    ctx.stroke();
    ctx.closePath();
  };

  const handleMouseUp = e => {
    mouseClicked = !mouseClicked;
  };

  const handleMouseMove = e => {
    findxy("down", e);
  };

  const findxy = (res, e) => {
    if (res === "down") {
      prevX = currX;
      prevY = currY;
      currX = e.clientX - canvas.current.offsetLeft;
      currY = e.clientY - canvas.current.offsetTop;

      console.log(canvas.current.offsetTop);
      flag = true;
      dot_flag = true;
      if (dot_flag) {
        ctx.beginPath();
        ctx.fillStyle = x;
        ctx.fillRect(currX, currY, 2, 2);
        ctx.closePath();
        dot_flag = false;
      }
    }

    if (res === "up" || res === "out") {
      flag = false;
    }

    if (res === "move") {
      if (flag) {
        prevX = currX;
        prevY = currY;
        currX = e.clientX - canvas.offsetLeft;
        currY = e.clientY - canvas.offsetTop;
        draw();
      }

      function draw() {
        ctx.beginPath();
        ctx.moveTo(prevX, prevY);
        ctx.lineTo(currX, currY);
        ctx.strokeStyle = x;
        ctx.lineWidth = y;
        ctx.stroke();
        ctx.closePath();
        // send draw data
        socket.emit("draw", { currX, currY, prevX, prevY });
      }
    }
  };

  useEffect(() => {
    setContext(canvas.current.getContext("2d"));
  }, [canvas]);

  return (
    <section className="Canvas">
      <canvas
        ref={canvas}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        Please update your browser
      </canvas>
    </section>
  );
}

export default Canvas;
