import React, { useRef, useState, useEffect } from "react";
import "./Canvas.scss";
import { isMobile } from "react-device-detect";
import CanvasDraw from "react-canvas-draw";

import io from "socket.io-client";

function Canvas() {
  const canvas = useRef();
  const [saved, updatedSaved] = useState();
  const [socket, updateSocket] = useState();

  useEffect(() => {
    updateSocket(io.connect("http://localhost:5000/"));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.on("updateData", data => {
        if (data) {
          console.log("drawing");
          canvas.current.loadSaveData(data, true);
        }
      });
    }
  });

  const undo = () => {
    canvas.current.undo();
  };

  const handleOnChange = () => {
    updatedSaved(canvas.current.getSaveData());
  };

  return (
    <section className="Canvas">
      {!isMobile ? (
        <div
          onMouseUp={() => {
            console.log("sending data");
            socket.emit("drawingData", saved);
          }}
        >
          <CanvasDraw
            ref={canvas}
            canvasWidth={1200}
            canvasHeight={550}
            disabled={false}
            lazyRadius={0}
            hideInterface={false}
            onChange={handleOnChange}
          />
          <button onClick={undo}>Undo</button>
        </div>
      ) : (
        <h1>Mobile not supported</h1>
      )}
    </section>
  );
}

export default Canvas;
