import React, { useRef, useEffect, useContext, useState } from "react";

import "./Canvas.scss";
import CanvasDraw from "react-canvas-draw";

import Chat from "../Chat/Chat";
import UserList from "../UserList/UserList";
import SocketContext from "../../context";
import { useSelector, useDispatch } from "react-redux";
import { useHistory } from "react-router";
import TimerClock from "../TimerClock/TimerClock";
import WordBlock from "../WordBlock/WordBlock";

// let socket;

function Canvas() {
  let roomUsers = useSelector(state => state.roomUsersReducer);
  const [canDraw, setCanDraw] = useState();
  let { socket } = useContext(SocketContext);
  const localStorageData = JSON.parse(localStorage.getItem("loginUserInfo"));
  const { name, room } = useSelector(state => state.contactReducer);
  const history = useHistory();
  const googleUserInfo = localStorageData;
  const canvas = useRef();
  const dispatch = useDispatch();

  useEffect(() => {
    console.log(roomUsers);
  }, [roomUsers, canDraw]);

  useEffect(() => {
    if (!googleUserInfo) {
      socket.off();
      history.push("/login");
      alert("Login first please!");
    } else {
      socket.emit(
        "join",
        { name: googleUserInfo.name, room, googleUserInfo },
        err => {
          alert(err || "not sure of the error");
          socket.emit("disconnectUser", googleUserInfo);
          socket.off();
          history.push("/");
        }
      );
    }

    socket.on("updateData", data => {
      if (canvas.current && data) {
        canvas.current.loadSaveData(data, true);
      }
    });

    socket.on("newJoinNotification", name => {
      dispatch({ type: "SET_NOTIFICATION", payload: true });
      dispatch({ type: "SET_MESSAGE", payload: `${name} has joined the room` });
    });
  }, [room, name, socket, localStorageData, history, googleUserInfo, dispatch]);

  useEffect(() => {
    return () => {
      console.log("will unmount");
      socket.emit("disconnectUser", googleUserInfo);
    };
  }, [socket]);

  return (
    <section className="Canvas">
      <div className="Container">
        <Chat socket={socket} />
        <div
          className="CanvasContainer"
          onMouseUp={() => {
            socket.emit("drawingData", {
              data: canvas.current.getSaveData(),
              room
            });
          }}
        >
          <CanvasDraw
            ref={canvas}
            disabled={false}
            brushRadius={6}
            canvasWidth={900}
            canvasHeight={600}
            lazyRadius={0}
            hideInterface={false}
          />
          <UserList />
          <TimerClock />
          <WordBlock />
        </div>
      </div>
    </section>
  );
}

export default Canvas;
